const GET_FAQ_GROUPS = "GET-FAQ-GROUPS"
import faqAPI from "../../api"
let initialState ={
 groups :[]
}

const FAQReducer = (state=initialState, actions) =>{
    switch (actions.type) {
        case GET_FAQ_GROUPS:
            return {...state, groups: actions.payload}
    }
}

const getFaqGroupsSuccess = (payload) => ({type: GET_FAQ_GROUPS, payload})

export const getFaqGroups = () => {
    return async dispatch => {
        let payload = await faqAPI.getFaq_Group();
            if(payload.data.resultCode === 0){
                dispatch(getFaqGroupsSuccess(payload.data))
            }

    }
}
