import React from 'react'
import {connect} from 'react-redux'
import {getContent} from '../../redux/reducers/ContentReducer'
import { Content } from './content'
import Preloader from '../Preloader/preloader'
import {AppStateType} from "../../redux/redux_store";

type PropsType = {
    getContent: () => any,
    content : Array<{
    id: number,
    Title: string,
    BrowserTitle: string,
    MetaDescription: string,
    UrlSlug: string,
    Content: string
}>,
}

class ContentContainer extends React.PureComponent<PropsType> {
    componentDidMount(){

        this.props.getContent()
    }
    render() {
        debugger
        return(
           this.props.content.length < 1 ? <Content  content = {this.props.content}/> : <Preloader/>
        )
    }
}
let mapStateToProps = (state: AppStateType) =>{
    return {
        content: state.content.content
    }
   }
export default connect(mapStateToProps, {getContent})(ContentContainer)
