import {faqAPI} from "../../api"
import  * as  axios from 'axios'

const GET_FAQ_GROUPS = "GET-FAQ-GROUPS"

let initialState ={
 groups :[]
}

export const FAQReducer = (state=initialState, action) =>{
    switch (action.type) {
        case GET_FAQ_GROUPS:
            debugger
            return {...state, groups: action.payload}
        default:
             return state
    }
}

const getFaqGroupsSuccess = (payload) => ({type: GET_FAQ_GROUPS, payload})

export const getFaqGroups = () => dispatch => {
    axios.get("/api/faq_category/")
}
