import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import s from './FAQ.module.css'
import {getFaqGroups, getFaqItems} from '../../redux/reducers/FAQReducer'
class FAQ_Group extends Component {
    componentDidMount() {
        debugger
        this.props.getFaqGroups()
        this.props.getFaqItems()
        }

        render(){
            const FAQ_complect = ({Title, id}) => {
                debugger
                const items = this.props.items.filter(i => i.Group === id)
                return <div>
                    <div className="row border-bottom ml-3 mb-3"> <h5 key={item.id} className={s.group}>{Title}</h5></div>
            {items.map(item=> <div key={item.id}><h6 className={s.group}>{item.Title}<i className="fas fa-chevron-down"></i></h6><p className={s.group, s.dropdown}>{item.Answer}</p></div>)}
                </div>
            }
            return <Fragment>
                <h3 className="container mt-3 mb-3">FAQ</h3>
                <p className="middle">In a professional contex it often happens that private or corporate</p>
                {this.props.groups.map(g => <FAQ_complect Title = {g.Title} id={g.id}/>)}
                  </Fragment>
}
}
 const mapStateToProps= (state) => {
    return {
        groups: state.FAQ.groups,
        items : state.FAQ.items
    }
}

export default connect(mapStateToProps, {getFaqGroups, getFaqItems})(FAQ_Group)
