import  * as  axios from 'axios'

let $crf_token = ('[name="csrfmiddlewaretoken"]').attr('value');

const instance =  axios.create({
    baseURL: 'http://127.0.0.1:8000/',
    headers: {"X-CSRFToken": $crf_token}


});

export const faqAPI =  {
    getFaq_Group: ()=>{
        return instance('api/faq_group/')},
    getFaq_Item: ()=> {
        return instance.get('api/faq_item')},
    active_Item: (item) => {
        return instance.put(`api/faq_item/${item.id}/`, {
            Group: item.Group,
            Title: item.Title,
            Answer: item.Answer,
            Active: item.Active,
        })
}}
export const authAPI = {
    getUser: (tokenConfig) => {
        return instance.get('rest-auth/user/', tokenConfig)
    },
    login: (body, config) => {
        return instance.post('rest-auth/login/', body, config)
    },
    logout: (tokenConfig) => {
        return instance.post('rest-auth/logout/', null, tokenConfig)
    },
    register: (body, config) => {
        return instance.post('rest-auth/registration/', body, config)
    }
}
