import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import s from './FAQ.module.css'
import cn from "classnames";
import {getFaqGroups, getFaqItems, ActiveId} from '../../redux/reducers/FAQReducer'
class FAQ_Group extends Component {
    componentDidMount() {
        this.props.getFaqGroups()
        this.props.getFaqItems()
        }
        constructor(props){
            super();
            this.state = {
                 active: false 
                };
        }

        render(){
            console.log(this.state.active)
            const FAQ_complect = ({Title, id}) => {
                const items = this.props.items.filter(i => i.Group === id)
                return <div>
                    <div className="row border-bottom ml-3 mb-3"> <h5 className={s.group}>{Title}</h5></div>
            {items.map(item=> <div>
                <h6 className={s.item,"d-flex justify-content-between d-flex align-items-center"}><span className="text-danger">{item.Title}</span>
            <div onClick={()=> this.props.ActiveId(item.id)} className={cn({ [s.active] : this.props.items[item.id+1].Active === true},s.menu_toogle)}>
            </div>
                </h6>
            <p className={cn({ [s.active] : this.state.active === true},s.group, s.dropdown)}>{item.Answer}</p>
            </div>)}
            </div>
            }
            return <Fragment>
                <h3 className="container mt-3 mb-3">FAQ</h3>
                <p className="middle">In a professional contex it often happens that private or corporate</p>
                {this.props.groups.map(g => <FAQ_complect Title = {g.Title} id={g.id}/>)}
                  </Fragment>
}
}
 const mapStateToProps= (state) => {
    return {
        groups: state.FAQ.groups,
        items : state.FAQ.items
    }
}

export default connect(mapStateToProps, {getFaqGroups, getFaqItems, ActiveId})(FAQ_Group)
