import { MessageActionTypes} from "./MessageReducer";
import {GetError} from "./ErrorsReducer";

export const USER_LOADING = 'USER_LOADING'
export const USER_LOADED = 'USER_LOADED'
export const LOGIN_SUCCESS = 'LOGIN_SUCCESS'
export const LOGOUT_SUCCESS = 'LOGOUT_SUCCESS'
export const REGISTER_SUCCESS = 'REGISTER_SUCCESS'
export const ACTIVATE_USER = 'ACTIVATE_USER'

interface UserLoading {
    type: typeof USER_LOADING
}

interface UserLoadedAction {
    type: typeof USER_LOADED,
    payload: {
        first_name: string,
        id: number,
        email: string
    } | null

}

interface LoginSuccessAction {
    type: typeof LOGIN_SUCCESS,
    payload: {
        auth_token: string | null
    }

}

interface LogoutSuccessAction{
    type: typeof LOGOUT_SUCCESS,
}

interface RegisterSuccessAction {
    type: typeof REGISTER_SUCCESS,
    payload: any
}

interface ActiveUserAction {
    type: typeof ACTIVATE_USER,
    payload: any

}


export type AuthActionTypes = ActiveUserAction| MessageActionTypes| GetError | RegisterSuccessAction | LogoutSuccessAction | LoginSuccessAction | UserLoadedAction | UserLoading
