import React from 'react'
import {connect} from 'react-redux'
import { EnterCode } from './enterCode'
import {reset_code} from '../../../redux/reducers/AuthReducer'
import { Redirect } from 'react-router-dom'



class EnterCodeContainer extends React.Component {

    render () {
        return (this.props.code? <Redirect to="/reset_password"/> : <EnterCode reset_code = {this.props.reset_code} />)
    }
}

const mapStateToProps = (state) => ({
    code: state.auth.code
})

export default connect(mapStateToProps, {reset_code})(EnterCodeContainer)
