import React from 'react'
import {gql} from "apollo-boost";
import {useQuery, useMutation} from 'react-apollo';
import s from "./FAQ/FAQ.module.css";
import cn from "classnames";

const Query_Groups = gql`
  query {
    FAQGroups {
        Title,
        id
    }
}`;

const Query_Item = gql`
  query {
    items{
        Title
        Group
        Answer
        Active
        id
    }
}`;

const Update_Item = gql`
mutation updateItem($Title: String $Group: ID $Active: Boolean $Answer: String){
  updateItem(id: $id ,input:{
    Title: $Title,
    Group: $Group,
    Answer: $Answer,
    Active: $Active
  }){
    item{
      Title
      Group{
        id
        Title
      }
      Answer
      Active
      id
    }
  }
}
`;

export const FrequentlyAskedQuestions = () => {
    let inputTitle, inputGroup, Answer, Active;

    const {groupsData, groupsLoading}: any = useQuery(
        Query_Groups, {
            pollInterval: 500 // refetch the result every 0.5 second
        }
    );
    const {itemsData, itemsLoading}: any = useQuery(
        Query_Groups, {
            pollInterval: 500 // refetch the result every 0.5 second
        }
    );
    const SuitableItems = (props: any) => {
        if (itemsLoading) return <p>Loading...</p>;
        const suitableItems = itemsData.items.filter((i: any) => Number(i.Group) === Number(props.Id))
        return(
            <div key={props.Id}>
                {suitableItems.map((item: any) => <div>
                    <h6 className={[s.item, "d-flex justify-content-between d-flex align-items-center"].join(' ')}><span
                        className="text-danger">{item.Title}</span>
                        <div
                             className={cn({[s.active]: item.Active}, s.menu_toogle)}>
                        </div>
                    </h6>
                    <p className={cn({[s.active]: item.Active}, s.group, s.dropdown)}>{item.Answer}</p>
                </div>)}
            </div>
        )
    }
    // const [createUser, {data}] = useMutation(Update_Item);
    if (groupsLoading) return <p>Loading...</p>;
    return (
        <div className={s.main}>
                <h3 className="container text-center mt-3 mb-3">FAQ</h3>
                <p className="middle text-center">In a professional context it often happens that private or
                    corporate</p>
            {groupsData.groups.map((group: any) =>
                <div>
                    <div className="row border-bottom ml-3 mb-3"><h5 className={s.group}>{group.Title}</h5></div>
                    <SuitableItems Id = {group.Group}/>
                </div>)}
            </div>
    )
}
