import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import s from './FAQ.module.css'
import {getFaqGroups} from '../../redux/reducers/FAQReducer'
class FAQ_Group extends Component {
    componentDidMount() {
        debugger
        this.props.getFaqGroups()
        }
        render(){
            return <Fragment>
                <h3 className="container mt-3 mb-3">FAQ</h3>
                <p className="middle">In a professional contex it often happens that private or corporate</p>

                {this.props.groups.map(g => <div className="row text-align-start"> <h4 className="col-12">{g.Title}</h4></div>)}

                  </Fragment>
}
}
 const mapStateToProps= (state) => {
    return {
        groups: state.FAQ.groups
    }
}

export default connect(mapStateToProps, {getFaqGroups})(FAQ_Group)
