import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import {getFaqGroups} from '../../redux/reducers/FAQReducer'
class FAQ_Group extends Component {
    componentDidMount() {
        this.props.getFaqGroups()
        }
        render(){
            return <Fragment>
                {this.props.groups.map(g => <div>{g.Title}</div>)}
                  </Fragment>
}
}
 const mapStateToProps= (state) => {
    return {
        groups: state.FAQ.groups
    }
}

export default connect(mapStateToProps, {getFaqGroups})(FAQ_Groge)
