import React from 'react'
import { forgotPassword } from './forgotPassword'
import {connect} from 'react-redux'
import {forgotpassword} from '../../../redux/reducers/AuthReducer'
import { Redirect } from 'react-router-dom'

class forgotPasswordContainer extends React.Component {

    render () {
        if (!this.props.forgot){return <forgotPassword forgotpassword ={this.props.forgotpassword} />}
        else if (this.props.forgot){
            return <Redirect to="code"/>
        }

    }
}

const mapStateToProps = (state) => ({
    forgot : state.auth.forgot
})

export default connect(mapStateToProps, {forgotpassword})(forgotPasswordContainer)
