import axios from "axios"


//import {authAPI} from "../../api"
const USER_LOADING = 'USER_LOADING'
const USER_LOADED = 'USER_LOADED'
const AUTH_ERROR = 'AUTH_ERROR'
const LOGIN_SUCCESS = 'LOGIN_SUCCESS'
const LOGIN_FAIL = 'LOGIN_FAIL'




let initialState ={
    token: localStorage.getItem('token'),
    isAuthenticated: null,
    isLoading: false,
    user: null
}

export const AuthReducer = (state=initialState, action) =>{
    switch (action.type) {
        case USER_LOADING :
            return {...state, isLoading: true}
        case USER_LOADED:
            return {...state, isAuthenticated: true, isLoading: false, user: action.payload}
        case AUTH_ERROR:
            localStorage.removeItem('token');
            return {...state, token: null, user:null, isAuthenticated: false, isLoading: false}
        case LOGIN_SUCCESS:
            debugger
            localStorage.setItem('token', action.payload.token)
            return {...state, ...action.payload, isAuthenticated: true, isLoading: false
            }
        case LOGIN_FAIL:
            localStorage.removeItem('token');
            return {...state, token: null, user:null, isAuthenticated: false, isLoading: false}
        default:
             return state
    }
}

const loadUserSuccess = () => ({type: USER_LOADING})
const loginSuccess = (payload) => ({type: LOGIN_SUCCESS, payload})

export const loadUser = () => (dispatch, getState) => {
    dispatch(loadUserSuccess());

    const token = getState().auth.token;

    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }

    if(token){
        config.headers['Authorization'] = `Token ${token}`;
    }
    axios.get('http://127.0.0.1:8000/api/auth/get_users', config)
    .then(response => {
        dispatch({
            type: USER_LOADED,
            payload: response.data
        });
    })
}
export const login = (username, password) => (dispatch) => {
    debugger
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({username, password})
    axios.post('http://127.0.0.1:8000/api/auth/login', body, config)
    .then(response => {
        debugger
        dispatch(loginSuccess(response.data));
    })
}
