import { profileAPI } from "../../api"
import {ProfileActionTypes, GET_CATEGORY, GET_COUNTRIES, GET_DOCUMENTS, GET_PROFILES, GET_SEARCHED_PROFILES} from "./ProfileTypes";
import {CREATE_MESSAGE} from "./MessageReducer";
import {ThunkAction} from "redux-thunk";
import {AppStateType} from "../redux_store";

type ThunkType = ThunkAction<Promise<void>, AppStateType, unknown, ProfileActionTypes>

type initialStateType =  {
    profiles: Array<{
        id: number,
        companyProfilePicture: string,
        companyName: string,
        companyDescription: string,
        country: string,
        companyLogo: string,
        sections: string,
        owner: number
    }> | any,
    countries: any,
    documents: Array<{
        id: number,
        Title: string,
        Thumbnail: string,
        Download: string,
        owner: number
    }> | any,
    category: any
}

let initialState: initialStateType = {
    profiles: [],
    countries: [],
    documents: [],
    category: []
};

export const ProfileReducer = (state = initialState, action: ProfileActionTypes) => {
    switch (action.type){
        case GET_COUNTRIES:
            return{...state, countries: action.payload}
        case GET_PROFILES:
            return {...state, profiles:  action.payload}
        case GET_DOCUMENTS:
            return {...state, documents:  action.payload}
        case GET_CATEGORY:
            return {...state, category: action.payload}
        case GET_SEARCHED_PROFILES:
            return {...state, profiles:  action.payload}
        default: 
            return state 
         }
    }

const getCountriesSuccessful = (payload: any) => ({type: GET_COUNTRIES, payload})
const getProfilesSuccessful = (payload: Array<{
        id: number,
        companyProfilePicture: string,
        companyName: string,
        companyDescription: string,
        country: string,
        companyLogo: string,
        sections: string,
        owner: number
}>) => ({type: GET_PROFILES, payload})
const getDocumentsSuccessful = (payload: Array<{
        id: number,
        Title: string,
        Thumbnail: string,
        Download: string,
        owner: number
}>) => ({type: GET_DOCUMENTS, payload})
const getCategorySuccessful = (payload: Array<{
        id: number,
        Name: string
}>) => ({type: GET_CATEGORY, payload})
const getSearchedProfiles = (payload: Array<{
        id: number,
        companyProfilePicture: string,
        companyName: string,
        companyDescription: string,
        country: string,
        companyLogo: string,
        sections: string,
        owner: number
}>) => ({type: GET_SEARCHED_PROFILES, payload})

export const GetCountries = () => async(dispatch: any) => {
    const response = await profileAPI.getCountries()
    dispatch(getCountriesSuccessful(response.data))
}

export const GetProfiles = (): ThunkType => async(dispatch) => {
    const response = await profileAPI.getProfile()
    dispatch(getProfilesSuccessful(response.data))
}
export const updateManufacturer = (data: any, id: number): ThunkType => async (dispatch) => {
    profileAPI.PutProfile(data, id)
    dispatch(
            {
                type: CREATE_MESSAGE,
                payload: {registered: "Your Profile was Submitted!"}})
}
export const updateDocument = (data: any, id: number): ThunkType => async (dispatch) => {
    profileAPI.PatchDocuments(data, id)
    dispatch({
        type: CREATE_MESSAGE,
        payload: {registered: "Documents for your Profile were Submitted!"}})
}
export const postDocument = (id: number): ThunkAction<Promise<void>, AppStateType, unknown, ProfileActionTypes> => async (dispatch) => {
    await profileAPI.PostDocuments(id)
    const response = await profileAPI.getDocuments()
    dispatch(getDocumentsSuccessful(response.data))
    dispatch({
        type: CREATE_MESSAGE,
        payload: {registered: "One more document was added"}})

}
export const GetDocuments = (): ThunkType => async(dispatch) => {
    debugger
    const response = await profileAPI.getDocuments()
    dispatch(getDocumentsSuccessful(response.data))
}

export const GetCategory = (): ThunkType => async(dispatch) => {
    const response = await profileAPI.getCategory()
    dispatch(getCategorySuccessful(response.data))
}

export const GetSearchedData = (search: string): ThunkType => async(dispatch) => {
    debugger
    const response = await profileAPI.getSearcheddData(search)
    dispatch(getSearchedProfiles(response.data))
}
