import React from 'react'
import { useMutation } from 'react-apollo';
import { gql } from 'apollo-boost';

const CREATE_FAQGroup = gql`
  mutation createFAQGroup ($Title: String!){
    createFAQGroup (Title: $Title){
       Title
  }
}
`;

export function CreateFAQ() {
  let inputTitle: any;
  const [CreateFAQGroup, { data }  ] = useMutation(CREATE_FAQGroup);
  return (
    <div>
      <form
        onSubmit={e => {
            debugger
          e.preventDefault();
          CreateFAQGroup({ variables: {
            Title: inputTitle.value,

        } });
        inputTitle.value = '';


      }}
      style = {{ marginTop: '2em', marginBottom: '2em' }}
     >
     <label>Title: </label>
     <input className="form-control"
       ref={node => {
         inputTitle = node;
       }}
       style={{ marginRight: '1em' }}
     />


     <button type="submit" style={{ cursor: 'pointer' }}>Add a FAQGroup</button>
    </form>
   </div>
  );}
