export const CREATE_MESSAGE = "CREATE_MESSAGE"

type CreateMessage = {
    type: typeof CREATE_MESSAGE,
    payload: string
}

const initialState = {};

export const MessagesReducer = (state = initialState, action: CreateMessage) => {
    switch (action.type) {
        case CREATE_MESSAGE:
            return (state = action.payload);
        default:
            return state;
    }
}

export const createMessage = (msg: { log_in_ed: any}) => {
    return {
        type: CREATE_MESSAGE,
        payload: msg.log_in_ed,
    };
};

export const createRMessage = (msg: { registered: any}) => {
    debugger
    return {
        type: CREATE_MESSAGE,
        payload: msg.registered,
    };
};
