import  * as  axios from 'axios'
const instance =  axios.create({
    baseURL: 'http://127.0.0.1:8000/api/',


});

export const faqAPI =  {
    getFaq_Group: ()=>{
        return instance('faq_group/')},
    getFaq_Item: ()=> {
        return instance.get('faq_item')},
    active_Item: (item) => {
        return instance.put(`faq_item/${item.id}/`, {
            Group: item.Group,
            Title: item.Title,
            Answer: item.Answer,
            Active: item.Active,
        })
}}
export const authAPI = {
    getUser: (tokenConfig) => {
        return instance.get('auth/get_users', tokenConfig)
    }
}
