import React from 'react';
import './App.css';
import {Provider} from 'react-redux'
import Login from './component/accounts/Login/login'
import store from './redux/redux_store'
import FAQ_Group from './component/FAQ/FAQ_Group';
import {BrowserRouter, Route, Switch, Redirect} from 'react-router-dom'
import Register from './component/accounts/Register/register';
import PrivateRoute from './component/common/PrivateRoute';
import { loadUser } from './redux/reducers/AuthReducer';
import { transitions, positions, Provider as AlertProvider } from 'react-alert'
import AlertTemplate from 'react-alert-template-basic'
import Alerts from './component/layout/Alerts';
import { useAlert } from 'react-alert'

const options = {
  position: positions.TOP_CENTER,
  timeout: 5000,
  offset: '30px',
  transition: transitions.SCALE
}

class App extends React.Component {
  componentDidMount(){
    store.dispatch(loadUser(  ))
  }
  render(){
  return  <div className="App">
                <BrowserRouter>
                <Provider store={store}>
                <AlertProvider template={AlertTemplate} {...options}>  
                <Alerts/>
                  <Switch>
                <Route exact path='/register' component= {Register}/>
                <Route exact path='/login' component= {Login}/>
                <PrivateRoute exact path='/' component= {FAQ_Group}/>
                </Switch>
                </AlertProvider>  
                </Provider>
                </BrowserRouter>
          </div>}
}
export default App;
