import {GET_ERRORS} from './ErrorsReducer'
import {authAPI} from "../../api"
const USER_LOADING = 'USER_LOADING'
const USER_LOADED = 'USER_LOADED'
const AUTH_ERROR = 'AUTH_ERROR'
const LOGIN_SUCCESS = 'LOGIN_SUCCESS'
const LOGIN_FAIL = 'LOGIN_FAIL'
const REGISTER_FAIL = 'REGISTER_FAIL'
const LOGOUT_SUCCESS = 'LOGOUT_SUCCESS'
const REGISTER_SUCCESS = 'REGISTER_SUCCESS'




let initialState ={
    token: localStorage.getItem('token'),
    isAuthenticated: null,
    isLoading: false,
    user: null
}

export const AuthReducer = (state=initialState, action) =>{
    switch (action.type) {
        case USER_LOADING :
            return {...state, isLoading: true}
        case USER_LOADED:
            return {...state, isAuthenticated: true, isLoading: false, user: action.payload}
        case AUTH_ERROR:
            localStorage.removeItem('token');
            return {...state, token: null, user:null, isAuthenticated: false, isLoading: false}
        case LOGIN_SUCCESS:
        case REGISTER_SUCCESS:

            debugger
            localStorage.setItem('token', action.payload.key)
            return {...state, ...action.payload, isAuthenticated: true, isLoading: false
            }
        case LOGOUT_SUCCESS:
        case REGISTER_FAIL:
        case LOGIN_FAIL:
            localStorage.removeItem('token');
            return {...state, token: null, user:null, isAuthenticated: false, isLoading: false}
        default:
             return state
    }
}

const loadUserSuccess = () => ({type: USER_LOADING})
const loginSuccess = (payload) => ({type: LOGIN_SUCCESS, payload})
const getUserSuccess = (payload) => ({type: USER_LOADED, payload})
const logoutSuccess = () => ({type: LOGOUT_SUCCESS})

export const loadUser = () => async (dispatch, getState) => {
    dispatch(loadUserSuccess());

    const response = await authAPI.getUser(tokenConfig(getState))
        dispatch(getUserSuccess(response.data));
    }

export const logout = () => async (dispatch, getState) => {
    debugger
    await authAPI.logout(tokenConfig(getState))
        dispatch(logoutSuccess);
    }
export const login = (email, password) => async (dispatch) => {
    debugger
    const config = {
        headers: {
            'Content-Type': 'application/json',

        }
    }
    const body = JSON.stringify({email, password})
    try {let response = await authAPI.login(body, config)
    dispatch(loginSuccess(response.data))} catch(err){
        const error = {
            msg: err.response.data,
            status: err.response.status}
            dispatch({
                type: GET_ERRORS
            })


    }
}

export const register = ({ email, first_name, last_name,  password1, password2}) => async (dispatch) => {
    debugger
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({first_name, last_name, password1, password2, email})
    const response = await authAPI.register(body, config)
        dispatch(loginSuccess(response.data));
}

export const tokenConfig = getState => {
        const token = getState().auth.token;

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        if(token){
            config.headers['Authorization'] = `Token ${token}`;
        }


        return config
}
