import React, {Component} from 'react'
import {connect} from 'react-redux'
import s from './FAQ.module.css'
import cn from "classnames";
import {getFaqGroups, getFaqItems, ActiveId} from '../../redux/reducers/FAQReducer'
import {faqAPI} from "../../api/FaqApi";
import {useQuery} from 'react-apollo';
import {gql} from 'apollo-boost';

const QUERY_FAQItem = gql`
  query {
    item {
        Group{
            id,
            Title
        }
        Title
        Answer
        Active
        id
    }
}`;

type RootPropsType = {
    getFaqGroups: () => void,
    getFaqItems: () => void,
    active_item: any,
    ActiveId: (item: {
        id: number,
        Title: string,
        Answer: string,
        Active: boolean,
        Group: number
    }) => void,
    items: Array<{
        id: number,
        Title: string,
        Answer: string,
        Active: boolean,
        Group: number
    }>,
    key: any,
    Groups: Array<{
        id: number
        Title: string
    }>
}

const FAQItemInfo = () => {
    const {data, loading} = useQuery(
        QUERY_FAQItem, {
            pollInterval: 500 // refetch the result every 0.5 second
        }
    );

    if (loading) return <p>Loading...</p>;

    // @ts-ignore
    return (
        <div>
            {data.item.map(({Group, id}: { Group: { Title: string, id: number }, id: number }) => (
                <div key={id}>
                    <p>
                        FaqGroup - {id} {Group.id}
                    </p>
                </div>))}
        </div>

    );
}

type StateType = {}

class FAQ_Group extends Component<RootPropsType> {
    componentDidMount() {
        this.props.getFaqGroups()
        this.props.getFaqItems()
    }


    // componentDidUpdate(prevProps: RootPropsType, prevState: StateType, snapshot: any) {
    //     debugger
    //
    //     if (this.props.active_item !== prevProps.active_item) {
    //         faqAPI.active_Item(this.props.active_item)
    //     }
    // }

    render() {

        const ActivateItem = (item: {
            id: number, Title: string, Answer: string, Active: boolean, Group: number
        }) => {
            item.Active = !item.Active
            this.props.ActiveId(item)
        }
        const OnClick = (item: {
            id: number,
            Title: string,
            Answer: string,
            Active: boolean,
            Group: number
        }) => {
            ActivateItem(item)
        }
        type FaqComplectPropsType = {
            Title: string,
            id: number
        }
        const FaqComplect = (props: FaqComplectPropsType) => {
            const {data, loading} = useQuery(
                QUERY_FAQItem, {
                    pollInterval: 500 // refetch the result every 0.5 second
                }
            );
            if (loading) return <p>Loading...</p>;
            const items = data.item.filter((i: any) => Number(i.Group.id) === props.id)
            console.log(items)

            return (<div key={this.props.key}>

                <div className="row border-bottom ml-3 mb-3"><h5 className={s.group}>{props.Title}</h5></div>
                {items.map((item: any) => <div>
                    <h6 className={[s.item, "d-flex justify-content-between d-flex align-items-center"].join(' ')}><span
                        className="text-danger">{item.Title}</span>
                        <div onClick={() => OnClick({
                            id: item.id,
                            Title: item.Title,
                            Answer: item.Answer,
                            Active: item.Active,
                            Group: item.Group.id
                        })}
                             className={cn({[s.active]: item.Active}, s.menu_toogle)}>
                        </div>
                    </h6>
                    <p className={cn({[s.active]: item.Active}, s.group, s.dropdown)}>{item.Answer}</p>
                </div>)}
            </div>)
        }
        return (
            <div className={s.main}>
                <h3 className="container text-center mt-3 mb-3">FAQ</h3>
                <p className="middle text-center">In a professional context it often happens that private or
                    corporate</p>
                {this.props.Groups.map((g, index) => (<FaqComplect key={index} Title={g.Title} id={g.id}/>))}

            </div>)
    }
}

const mapStateToProps = (state: any) => {
    return {
        Groups: state.FAQ.groups,
        items: state.FAQ.items,
        active_item: state.FAQ.active_item

    }
}

export default connect(mapStateToProps, {getFaqGroups, getFaqItems, faqAPI, ActiveId})(FAQ_Group)
