import React from 'react'
import { forgotPassword } from './forgotPassword'
import {connect} from 'react-redux'
import {forgotpassword} from '../../../redux/reducers/AuthReducer'
import { Redirect } from 'react-router-dom'

class forgotPasswordContainer extends React.Component {
    componentDidMount(){
        this.props.forgot && <Redirect to="/code" />
    }
    render () {
        return <forgotPassword forgotpassword ={this.props.forgotpassword} />
    }
}

const mapStateToProps = (state) => ({
    forgot : state.auth.forgot
})

export default connect(mapStateToProps, {forgotpassword})(forgotPasswordContainer)
