//import {faqAPI} from "../../api"
import  * as  axios from 'axios'

const GET_FAQ_GROUPS = "GET-FAQ-GROUPS"
const GET_FAQ_ITEM = "GET-FAQ-ITEM"

let initialState ={
 groups :[],
 items :[]
}

export const FAQReducer = (state=initialState, action) =>{
    switch (action.type) {
        case GET_FAQ_GROUPS:
            return {...state, groups: action.payload}
        case GET_FAQ_ITEM:
            return {...state, items: action.payload}
        default:
             return state
    }
}

//const getFaqGroupsSuccess = (payload) => ({type: GET_FAQ_GROUPS, payload})

export const getFaqGroups = () => async dispatch => {
    let response = await axios.get("http://127.0.0.1:8000/api/faq_group/")
    dispatch(getFaqGroups(response.data))
    }

export const getFaqItems = () => dispatch => {
    axios.get("http://127.0.0.1:8000/api/faq_item/").then(response => {
        dispatch({
            type: GET_FAQ_ITEM,
            payload: response.data
        })
    }).catch(err => console.log(err));
}
